MoviesFromSearchResponse moviesFromSearchResponseFromJson(String str) => MoviesFromSearchResponse.fromJson(json.decode(str));
String moviesFromSearchResponseToJson(MoviesFromSearchResponse data) => json.encode(data.toJson());
class MoviesFromSearchResponse {
  MoviesFromSearchResponse({
      this.page, 
      this.results, 
      this.totalPages, 
      this.totalResults,});

  MoviesFromSearchResponse.fromJson(dynamic json) {
    page = json['page'];
    if (json['results'] != null) {
      results = [];
      json['results'].forEach((v) {
        results?.add(Movies.fromJson(v));
      });
    }
    totalPages = json['total_pages'];
    totalResults = json['total_results'];
  }
  num? page;
  List<Movies>? results;
  num? totalPages;
  num? totalResults;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['page'] = page;
    if (results != null) {
      map['results'] = results?.map((v) => v.toJson()).toList();
    }
    map['total_pages'] = totalPages;
    map['total_results'] = totalResults;
    return map;
  }

}